
$(document).ready(function(){const button=$('#button');const modal=$('#modal');const close=$('#close');button.on('click',function(){modal.addClass('modal_active');});close.on('click',function(){modal.removeClass('modal_active');});const top_show=150;const delay=1000;$(window).scroll(function(){if($(this).scrollTop()>top_show)$('#top').fadeIn();else $('#top').fadeOut();});$('#top').click(function(){$('body, html').animate({scrollTop:0},delay);});const wow=new WOW({mobile:false});wow.init();$(".owl-carousel").owlCarousel({loop:true,margin:30,nav:true,navText:["<img src='img/portfolio/left-arrow.png'>","<img src='img/portfolio/right-arrow.png'>"],responsive:{0:{items:1},768:{items:1,margin:30},992:{items:3,margin:30}}});});